function Shape(){this.color="rgba("+Math.floor(Math.random()*255)+", "+Math.floor(Math.random()*255)+","+Math.floor(Math.random()*255)+", 0.5)"}function Circle(n,t){Shape.call(this);this.x=n;this.y=t;this.radius=Math.random()*2;this.maxRadius=Math.random()*canvas.width/2;this.inc=1}function Blob(n,t){Shape.call(this);this.pts=[{x:n,y:t}]}Circle.prototype=Object.create(Shape.prototype);Circle.prototype.update=function(){this.radius+=this.inc;this.radius>this.maxRadius?this.inc=-3:this.radius<5&&(this.inc=1,this.radius<0&&(this.radius=Math.random()*2))};Circle.prototype.draw=function(n){n.beginPath();n.fillStyle=this.color;n.arc(this.x,this.y,this.radius,0,2*Math.PI);n.fill();n.closePath()};Blob.prototype=Object.create(Shape.prototype);Blob.prototype.draw=function(n){var t,i,r;for(n.fillStyle=this.color,n.beginPath(),n.moveTo(this.pts[0].x,this.pts[0].y),t=1;t<this.pts.length-2;t++)i=(this.pts[t].x+this.pts[t+1].x)/2,r=(this.pts[t].y+this.pts[t+1].y)/2,n.quadraticCurveTo(this.pts[t].x,this.pts[t].y,i,r);n.fill();n.closePath()};Blob.prototype.update=function(){for(var n,i=Math.floor(Math.random()*this.pts.length),t=0;t<i;t++)n=Math.floor(Math.random()*this.pts.length),this.pts[n].x+=Math.random()*10-5,this.pts[n].y+=Math.random()*10-5};Blob.prototype.addPoint=function(n,t){this.pts.push({x:n,y:t})};